import React from 'react'
import { useState, useEffect } from 'react'
import './Task'
import './App.css'
const App = () =>{

    const newTask = "ahmed"

    const [seconds, setSeconds] = useState(25*60)
    const [task, setTask] = useState()

 

    useEffect(()=>{

        task = []


        const countDown = setInterval(()=>{
            if (seconds > 0){
                setSeconds(seconds-1);
            }
        }, 1000)

        return () => clearInterval(countDown);
    }, [seconds])

    const editTime = (time) =>{
        return time < 10? `0${time}` : time
    }

    const minutes = Math.floor(seconds/60)
    const remainSeconds = seconds % 60



    return (
        <>
            <div className='columns'>
                <div className='column is-one-third Tasks-Container'>
                    <div className='container'>
                        <div className='section'>
                            <div className='container'>
                                <h1 className = 'is-size-1 has-text-centered'>Pomodoro</h1>
                            </div>
                        </div>

                        <div className='section Timer-Section'>
                            <div className='container'>

                                {
                                    tasks.map((task)=>{
                                        <Task/>
                                    })
                                }
                                

                                <div className='Taskcard Task-Add'>
                                    <div className='Task-Size-Overlay'>
                                        <span className='Task-Size'>+</span>
                                    </div>
                                    <form>
                                        <div className='Task-Content'>
                                            <span className='Task-Tag'>#Study</span>
                                            <input className='Task-Title Task-Input is-size-5' value={task} 
                                            onChange={(e)=>{
                                                setTask(e.target.value);
                                            }}/>
                                        </div>
                                        <button className='button is-white' type='submit'
                                            onClick={()=>{
                                                // tag task
                                            }}
                                        >Add</button>
                                    </form>

                                </div>
                            </div>
                        </div>
                    </div>   
                </div>


                <div className='column Timer-Container '>
                    <div className='contianer'>
                        <div className='section'>
                            <div className='container'>
                                zerothing
                            </div>
                        </div>

                        <div className='section'>
                            <div className='container'>
                                <h1 className='Timer has-text-centered'>{editTime(minutes)}:{editTime(remainSeconds)}</h1>
                            </div>
                        </div>

                        <div className='section'>
                        onething
                        </div>
                    </div>
                </div>
            </div>
            
        
        </>
    )
}

export default App
