import React, { useState, useEffect } from 'react';
import './App.css';
import Task from './Task';

const pomsize = 3; // Initial countdown time in seconds

const App = () => {
    const [seconds, setSeconds] = useState(pomsize);
    const [tasks, setTasks] = useState([]);
    const [taskInput, setTaskInput] = useState('');
    const [currentTask, setCurrentTask] = useState({
        "isselected": false,
        "isdone": false,
        "Title": ""
    });

    // Function to handle task submission
    const submitTask = () => {
        if (taskInput.trim() !== '') {
            // Add new task to tasks array
            setTasks([...tasks, taskInput]);
            // Set current task to the newly submitted task
            setCurrentTask({
                "isselected": true,
                "isdone": false,
                "Title": taskInput
            });
            // Clear input field
            setTaskInput('');
            // Reset countdown timer to initial value (pomsize seconds)
            setSeconds(pomsize);
        }
    };

    // Countdown timer effect
    useEffect(() => {
        const countDown = setInterval(() => {
            if (seconds > 0) {
                setSeconds(seconds - 1);
            } else {
                // Timer reaches 0
                clearInterval(countDown);
                // Mark current task as done if it was selected and not already done
                if (currentTask.isselected && !currentTask.isdone) {
                    setCurrentTask(prevTask => ({
                        ...prevTask,
                        "isdone": true
                    }));
                }
            }
        }, 1000);

        // Cleanup interval on component unmount
        return () => clearInterval(countDown);
    }, [seconds, currentTask]);

    // Format time utility function
    const formatTime = (time) => {
        return time < 10 ? `0${time}` : time;
    };

    const minutes = Math.floor(seconds / 60);
    const remainSeconds = seconds % 60;

    return (
        <div className='columns'>
            {/* Left column for tasks */}
            <div className='column is-one-third Tasks-Container'>
                <div className='container'>
                    <div className='section'>
                        <div className='container'>
                            <h1 className='is-size-1 has-text-centered'>Pomodoro</h1>
                        </div>
                    </div>

                    <div className='section Timer-Section'>
                        <div className='container'>
                            {/* Display tasks */}
                            {tasks.length > 0 && (
                                <div className='TaskList'>
                                    {tasks.map((task, index) => (
                                        <div key={index} onClick={() => {
                                            // Set current task to the clicked task
                                            setCurrentTask({
                                                "isselected": true,
                                                "isdone": false,
                                                "Title": task
                                            });
                                            // Reset countdown timer to initial value (pomsize seconds)
                                            setSeconds(pomsize);
                                        }} className={currentTask.isdone ? 'strikethrough' : ''}>
                                            <Task title={task} />
                                        </div>
                                    ))}
                                </div>
                            )}

                            {/* Task input and add button */}
                            <div className='Taskcard Task-Add'>
                                <div className='Task-Size-Overlay'>
                                    <span className='Task-Size'>+</span>
                                </div>
                                <div className='Task-Content'>
                                    <span className='Task-Tag'>#Study</span>
                                    <input
                                        className='Task-Title Task-Input is-size-5'
                                        value={taskInput}
                                        onChange={(e) => setTaskInput(e.target.value)}
                                        placeholder='Enter task title'
                                    />
                                </div>
                                <button className='button is-white' onClick={submitTask}>
                                    Add
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Right column for timer */}
            <div className='column Timer-Container'>
                <div className='container'>
                    <div className='section'>
                        <div className='container'>zerothing</div>
                    </div>

                    <div className='section'>
                        <div className='container'>
                            <h1 className='Timer has-text-centered'>
                                {formatTime(minutes)}:{formatTime(remainSeconds)}
                            </h1>
                        </div>
                    </div>

                    <div className='section'>onething</div>
                </div>
            </div>
        </div>
    );
};

export default App;
