import React from "react";
import { useState } from "react";


const Task = ({title, counter}) =>{


    const [clicked, setClicked] = useState(false);

    const handleClick = () =>{
      setClicked(!clicked);
    }


    return(
        <>
            <div className={clicked?'Taskcard clicked':'Taskcard'} onClick={()=>{
                handleClick();
            }}>
                <div className='Task-Size-Overlay'>
                    <span className='Task-Size'>{counter}</span>
                </div>
                <div className='Task-Content'>
                    <span className='Task-Tag'>#Study</span>
                    <div>
                        <span className='Task-Title is-size-5'>{title}</span>
                    </div>

                </div>
            </div>
        </>
    )
}

export default Task;
