import React, { useState, useEffect } from 'react';
const CountdownTimer = ({ hours = 0, minutes = 0, seconds = 0 }) => {
  const [remainingTime, setRemainingTime] = useState({ hours, minutes, seconds });

  useEffect(() => {
    const interval = setInterval(() => {
      if (remainingTime.hours === 0 && remainingTime.minutes === 0 && remainingTime.seconds === 0) {
        clearInterval(interval);
      } else {
        const updatedTime = calculateTime(remainingTime);
        setRemainingTime(updatedTime);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [remainingTime]);

  const calculateTime = (time) => {
    let updatedHours = time.hours;
    let updatedMinutes = time.minutes;
    let updatedSeconds = time.seconds - 1;

    if (updatedSeconds < 0) {
      updatedMinutes -= 1;
      updatedSeconds = 59;
    }
    if (updatedMinutes < 0) {
      updatedHours -= 1;
      updatedMinutes = 59;
    }

    return { hours: updatedHours, minutes: updatedMinutes, seconds: updatedSeconds };
  };

  return (
    <div>
      <h1>Countdown Timer</h1>
      <div>
        <span>{remainingTime.hours.toString().padStart(2, '0')}:</span>
        <span>{remainingTime.minutes.toString().padStart(2, '0')}:</span>
        <span>{remainingTime.seconds.toString().padStart(2, '0')}</span>
      </div>
    </div>
  );
};

export default CountdownTimer;
