import React, { useState, useEffect } from 'react';
import './App.css';
import Task from './Task'
const App = () => {
    const [seconds, setSeconds] = useState(25 * 60);
    const [tasks, setTasks] = useState([]);
    const [taskInput, setTaskInput] = useState('');
    const [currenTask, setCurrentTask] = useState()
    // Function to handle task submission
    const submitTask = () => {
        if (taskInput.trim() !== '') {
            setTasks([...tasks, taskInput]); // Add new task to tasks array
            setTaskInput(''); // Clear input field
        }
    };

    const currentTask = (task) =>{
        setCurrentTask(task);
        if (seconds == 0){
            alert('DONE')
        }
    }

    // Countdown timer effect
    useEffect(() => {
        const countDown = setInterval(() => {
            if (seconds > 0) {
                setSeconds(seconds - 1);
            }
        }, 1000);

        return () => clearInterval(countDown); // Cleanup interval on component unmount
    }, [seconds]);

    // Format time utility function
    const formatTime = (time) => {
        return time < 10 ? `0${time}` : time;
    };

    const minutes = Math.floor(seconds / 60);
    const remainSeconds = seconds % 60;

    return (
        <div className='columns'>
            {/* Left column for tasks */}
            <div className='column is-one-third Tasks-Container'>
                <div className='container'>
                    <div className='section'>
                        <div className='container'>
                            <h1 className='is-size-1 has-text-centered'>Pomodoro</h1>
                        </div>
                    </div>

                    <div className='section Timer-Section'>
                        <div className='container'>
                            {/* Display tasks */}
                            {tasks.length > 0 && (
                                <div className='TaskList'>
                                    {tasks.map((task, index) => (
                                        <div onClick={()=>{
                                            setSeconds(25*60);
                                            currentTask(task)
                                        }}>
                                            <Task title={task} id='2'/>
                                        </div>
                                    ))}
                                </div>
                            )}

                            {/* Task input and add button */}
                            <div className='Taskcard Task-Add'>
                                <div className='Task-Size-Overlay'>
                                    <span className='Task-Size'>+</span>
                                </div>
                                <div className='Task-Content'>
                                    <span className='Task-Tag'>#Study</span>
                                    <input
                                        className='Task-Title Task-Input is-size-5'
                                        value={taskInput}
                                        onChange={(e) => setTaskInput(e.target.value)}
                                        placeholder='Enter task title'
                                    />
                                </div>
                                <button className='button is-white' onClick={submitTask}>
                                    Add
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Right column for timer */}
            <div className='column Timer-Container'>
                <div className='container'>
                    <div className='section'>
                        <div className='container'>zerothing</div>
                    </div>

                    <div className='section'>
                        <div className='container'>
                            <h1 className='Timer has-text-centered'>
                                {formatTime(minutes)}:{formatTime(remainSeconds)}
                                {currentTask}
                            </h1>
                        </div>
                    </div>

                    <div className='section'>onething</div>
                </div>
            </div>
        </div>
    );
};

export default App;
